function setTimeNow(){var t,e,o,r,s,n,a=new Date;t=a.getHours(),e=a.getMinutes(),o=a.getSeconds(),t>=12&&(t-=12);var i=60*t*60,c=60*e,d=o,u=43200,g=3600,m=60;r=(i+c+d)/u*360,s=(c+d)/g*360,n=d/m*360,$("#pivotHour").css({transform:"rotate("+r+"deg)"}),$("#pivotMinute").css({transform:"rotate("+s+"deg)"}),$("#pivotSecond").css({transform:"rotate("+n+"deg)"})}$(document).ready(function(){setTimeNow()});